// src/pages/ManageEvents.js
import React, { useState } from 'react';
import '.ManafeEvents.css'

function ManageEvents({ role }) {
  const [events, setEvents] = useState([
    { id: 1, title: 'Capacitación de Riego Automático', date: '2024-11-20', time: '10:00 AM', description: 'Evento para aprender sobre riego automático.' },
    { id: 2, title: 'Sembrado Ecológico', date: '2024-11-22', time: '2:00 PM', description: 'Capacitación sobre técnicas de sembrado ecológico.' },
  ]);
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
  });

  const [appointment, setAppointment] = useState({
    eventId: '',
    date: '',
    time: '',
  });

  const handleEventChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const handleCreateEvent = () => {
    // Simula la creación de un evento
    setEvents([
      ...events,
      { ...newEvent, id: events.length + 1, date: newEvent.date, time: newEvent.time },
    ]);
    setNewEvent({ title: '', description: '', date: '', time: '' });
  };

  const handleAppointmentChange = (e) => {
    setAppointment({ ...appointment, [e.target.name]: e.target.value });
  };

  const handleScheduleAppointment = () => {
    // Simula la agendación de una cita
    alert(`Cita agendada para el evento: ${appointment.eventId}, Fecha: ${appointment.date}, Hora: ${appointment.time}`);
  };

  return (
    <div className="manage-events">
      <h1>Gestión de Eventos y Citas</h1>

      {role === 'admin' && (
        <div>
          <h2>Crear Nuevo Evento</h2>
          <input
            type="text"
            name="title"
            placeholder="Título del Evento"
            value={newEvent.title}
            onChange={handleEventChange}
          />
          <textarea
            name="description"
            placeholder="Descripción del Evento"
            value={newEvent.description}
            onChange={handleEventChange}
          />
          <input
            type="date"
            name="date"
            value={newEvent.date}
            onChange={handleEventChange}
          />
          <input
            type="time"
            name="time"
            value={newEvent.time}
            onChange={handleEventChange}
          />
          <button onClick={handleCreateEvent}>Crear Evento</button>
        </div>
      )}

      <h2>Eventos Disponibles</h2>
      <div>
        {events.map((event) => (
          <div key={event.id}>
            <h3>{event.title}</h3>
            <p>{event.description}</p>
            <p>{event.date} - {event.time}</p>

            {role === 'client' && (
              <div>
                <h3>Agendar Cita</h3>
                <input
                  type="date"
                  name="date"
                  value={appointment.date}
                  onChange={handleAppointmentChange}
                />
                <input
                  type="time"
                  name="time"
                  value={appointment.time}
                  onChange={handleAppointmentChange}
                />
                <button onClick={handleScheduleAppointment}>Agendar</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ManageEvents;
