import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from "@/components/ui/card";

function ManageEvents({ role }) {
  const [events, setEvents] = useState([
    { id: 1, title: 'Capacitaci√≥n de Riego Autom√°tico', date: '2024-11-20', time: '10:00 AM', description: 'Evento para aprender sobre riego autom√°tico.' },
    { id: 2, title: 'Sembrado Ecol√≥gico', date: '2024-11-22', time: '2:00 PM', description: 'Capacitaci√≥n sobre t√©cnicas de sembrado ecol√≥gico.' },
  ]);
  
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
  });

  const [appointment, setAppointment] = useState({
    eventId: '',
    date: '',
    time: '',
  });

  const handleEventChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const handleCreateEvent = () => {
    setEvents([
      ...events,
      { ...newEvent, id: events.length + 1, date: newEvent.date, time: newEvent.time },
    ]);
    setNewEvent({ title: '', description: '', date: '', time: '' });
  };

  const handleAppointmentChange = (e) => {
    setAppointment({ ...appointment, [e.target.name]: e.target.value });
  };

  const handleScheduleAppointment = () => {
    alert(`Cita agendada para el evento: ${appointment.eventId}, Fecha: ${appointment.date}, Hora: ${appointment.time}`);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-center mb-8 text-gray-800">
        Gesti√≥n de Eventos y Citas
      </h1>

      {role === 'admin' && (
        <Card className="mb-8">
          <CardHeader>
            <h2 className="text-2xl font-semibold text-gray-700">Crear Nuevo Evento</h2>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <input
                type="text"
                name="title"
                placeholder="T√≠tulo del Evento"
                value={newEvent.title}
                onChange={handleEventChange}
                className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              <textarea
                name="description"
                placeholder="Descripci√≥n del Evento"
                value={newEvent.description}
                onChange={handleEventChange}
                className="w-full p-2 border rounded-lg h-24 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              <div className="grid grid-cols-2 gap-4">
                <input
                  type="date"
                  name="date"
                  value={newEvent.date}
                  onChange={handleEventChange}
                  className="p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                <input
                  type="time"
                  name="time"
                  value={newEvent.time}
                  onChange={handleEventChange}
                  className="p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
              <button 
                onClick={handleCreateEvent}
                className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Crear Evento
              </button>
            </div>
          </CardContent>
        </Card>
      )}

      <h2 className="text-2xl font-semibold text-gray-700 mb-4">Eventos Disponibles</h2>
      <div className="grid md:grid-cols-2 gap-6">
        {events.map((event) => (
          <Card key={event.id} className="h-full">
            <CardHeader>
              <h3 className="text-xl font-semibold text-gray-700">{event.title}</h3>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <p className="text-gray-600">{event.description}</p>
                <div className="flex justify-between text-sm text-gray-500">
                  <span>üìÖ {event.date}</span>
                  <span>‚è∞ {event.time}</span>
                </div>

                {role === 'client' && (
                  <div className="mt-4 pt-4 border-t">
                    <h4 className="text-lg font-medium text-gray-700 mb-3">Agendar Cita</h4>
                    <div className="space-y-3">
                      <input
                        type="date"
                        name="date"
                        value={appointment.date}
                        onChange={handleAppointmentChange}
                        className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                      <input
                        type="time"
                        name="time"
                        value={appointment.time}
                        onChange={handleAppointmentChange}
                        className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                      <button 
                        onClick={handleScheduleAppointment}
                        className="w-full bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-colors"
                      >
                        Agendar
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

export default ManageEvents;
