import React, { useState } from 'react';
import './Login.css'; // Asegúrate de tener los estilos adecuados

const Login = () => {
  const [dato, setDato] = useState("DatoX"); // Valor predeterminado para el campo

  const handleLogin = (e) => {
    e.preventDefault();
    alert(`Iniciando sesión con el dato: ${dato}`);
  };

  return (
    <div className="login-container">
      <h2>Accede a tu cuenta</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="dato">Dato X:</label>
          <input 
            type="text" 
            id="dato" 
            value={dato} 
            onChange={(e) => setDato(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Iniciar sesión</button>
      </form>
    </div>
  );
};

export default Login;
