import React, { useState } from 'react';
import './ManageEvents.css';

function ManageEvents({ role }) {
  const [events, setEvents] = useState([
    { id: 1, title: 'Capacitación de Riego Automático', date: '2024-11-20', time: '10:00 AM', description: 'Evento para aprender sobre riego automático.' },
    { id: 2, title: 'Sembrado Ecológico', date: '2024-11-22', time: '2:00 PM', description: 'Capacitación sobre técnicas de sembrado ecológico.' },
  ]);

  const [appointments, setAppointments] = useState([
    { id: 1, eventId: 1, date: '2024-11-20', time: '10:00 AM' }, // Ejemplo de cita agendada
  ]);

  const [newAppointment, setNewAppointment] = useState({
    id: '', // ID de la cita, utilizado para editar
    eventId: '',
    date: '',
    time: '',
  });

  const [newEvent, setNewEvent] = useState({
    title: '',
    date: '',
    time: '',
    description: '',
  });

  const [editingEvent, setEditingEvent] = useState(null);

  const handleAppointmentChange = (e) => {
    setNewAppointment({ ...newAppointment, [e.target.name]: e.target.value });
  };

  const handleEventChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const handleScheduleAppointment = () => {
    if (newAppointment.id) {
      // Si ya tiene id, significa que es una edición
      setAppointments(
        appointments.map(appointment =>
          appointment.id === newAppointment.id ? newAppointment : appointment
        )
      );
    } else {
      // Si no tiene id, es una nueva cita
      setAppointments([
        ...appointments,
        { id: appointments.length + 1, ...newAppointment },
      ]);
    }
    setNewAppointment({ id: '', eventId: '', date: '', time: '' });
  };

  const handleScheduleEvent = () => {
    setEvents([
      ...events,
      { id: events.length + 1, ...newEvent },
    ]);
    setNewEvent({ title: '', date: '', time: '', description: '' });
  };

  const handleEditAppointment = (id) => {
    const appointmentToEdit = appointments.find(appointment => appointment.id === id);
    setNewAppointment({
      id: appointmentToEdit.id,
      eventId: appointmentToEdit.eventId,
      date: appointmentToEdit.date,
      time: appointmentToEdit.time,
    });
  };

  const handleDeleteAppointment = (id) => {
    setAppointments(appointments.filter(appointment => appointment.id !== id));
  };

  const handleDeleteEvent = (id) => {
    setEvents(events.filter(event => event.id !== id));
  };

  const handleEditEvent = (event) => {
    setEditingEvent(event);
    setNewEvent({
      title: event.title,
      date: event.date,
      time: event.time,
      description: event.description,
    });
  };

  const handleUpdateEvent = () => {
    setEvents(events.map(event =>
      event.id === editingEvent.id ? { ...editingEvent, ...newEvent } : event
    ));
    setEditingEvent(null);
    setNewEvent({ title: '', date: '', time: '', description: '' });
  };

  const isEditable = (appointmentDate) => {
    const today = new Date();
    const appointment = new Date(appointmentDate);
    const timeDiff = appointment - today; // Diferencia en milisegundos

    const daysLeft = timeDiff / (1000 * 3600 * 24); // Convertir milisegundos a días
    return daysLeft > 2; // Se puede editar solo si faltan más de 2 días
  };

  return (
    <div className="manage-events">
      <div className="manage-events-container">
        <h1>Gestión de Eventos y Citas</h1>

        {/* Módulo para Admin */}
        {role === 'admin' && (
          <div className="admin-section">
            <h3>{editingEvent ? 'Editar Evento' : 'Crear Nuevo Evento'}</h3>
            <input
              type="text"
              name="title"
              placeholder="Título del evento"
              value={newEvent.title}
              onChange={handleEventChange}
            />
            <input
              type="date"
              name="date"
              value={newEvent.date}
              onChange={handleEventChange}
            />
            <input
              type="time"
              name="time"
              value={newEvent.time}
              onChange={handleEventChange}
            />
            <textarea
              name="description"
              placeholder="Descripción del evento"
              value={newEvent.description}
              onChange={handleEventChange}
            />
            <button onClick={editingEvent ? handleUpdateEvent : handleScheduleEvent}>
              {editingEvent ? 'Actualizar Evento' : 'Crear Evento'}
            </button>

            <h2>Eventos Programados</h2>
            <div className="events-list">
              {events.map((event) => (
                <div key={event.id} className="event-card">
                  <h3>{event.title}</h3>
                  <p>Fecha: {event.date}</p>
                  <p>Hora: {event.time}</p>
                  <p>{event.description}</p>

                  <div className="event-actions">
                    <button onClick={() => handleEditEvent(event)}>Editar Evento</button>
                    <button onClick={() => handleDeleteEvent(event.id)}>Eliminar Evento</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Módulo para Cliente */}
        {role === 'client' && (
          <div className="appointment-section">
            <h3>{newAppointment.id ? 'Editar Cita' : 'Agendar Nueva Cita'}</h3>
            <select
              name="eventId"
              value={newAppointment.eventId}
              onChange={handleAppointmentChange}
            >
              <option value="">Selecciona un evento</option>
              {events.map(event => (
                <option key={event.id} value={event.id}>
                  {event.title}
                </option>
              ))}
            </select>
            <input
              type="date"
              name="date"
              value={newAppointment.date}
              onChange={handleAppointmentChange}
            />
            <input
              type="time"
              name="time"
              value={newAppointment.time}
              onChange={handleAppointmentChange}
            />
            <button onClick={handleScheduleAppointment}>
              {newAppointment.id ? 'Actualizar Cita' : 'Agendar Cita'}
            </button>
          </div>
        )}

        <h2>Citas Agendadas</h2>
        <div className="appointments-list">
          {appointments.map((appointment) => (
            <div key={appointment.id} className="appointment-card">
              <h3>Cita para el evento: {events.find(event => event.id === appointment.eventId)?.title}</h3>
              <p>Fecha: {appointment.date}</p>
              <p>Hora: {appointment.time}</p>

              <div className="event-actions">
                {isEditable(appointment.date) ? (
                  <>
                    <button onClick={() => handleEditAppointment(appointment.id)}>Editar</button>
                    <button onClick={() => handleDeleteAppointment(appointment.id)}>Eliminar</button>
                  </>
                ) : (
                  <p>No puedes editar esta cita porque la fecha está cerca.</p>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ManageEvents;
