import React, { useState } from 'react';
import './ManageEvents.css';

function ManageEvents({ role }) {
  const [events, setEvents] = useState([
    { id: 1, title: 'Capacitación de Riego Automático', date: '2024-11-20', time: '10:00 AM', description: 'Evento para aprender sobre riego automático.', appointments: [] },
    { id: 2, title: 'Sembrado Ecológico', date: '2024-11-22', time: '2:00 PM', description: 'Capacitación sobre técnicas de sembrado ecológico.', appointments: [] },
  ]);

  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
  });

  const [appointment, setAppointment] = useState({
    eventId: '',
    date: '',
    time: '',
  });

  const [editingEvent, setEditingEvent] = useState(null); // Estado para gestionar la edición de eventos

  const handleEventChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const handleCreateEvent = () => {
    if (editingEvent) {
      // Si estamos editando, actualizamos el evento
      const updatedEvents = events.map((event) =>
        event.id === editingEvent.id ? { ...event, ...newEvent } : event
      );
      setEvents(updatedEvents);
      setEditingEvent(null);
    } else {
      // Si no estamos editando, creamos un nuevo evento
      setEvents([
        ...events,
        { ...newEvent, id: events.length + 1, appointments: [] },
      ]);
    }
    setNewEvent({ title: '', description: '', date: '', time: '' });
  };

  const handleAppointmentChange = (e) => {
    setAppointment({ ...appointment, [e.target.name]: e.target.value });
  };

  const handleScheduleAppointment = (eventId) => {
    const eventToUpdate = events.find((event) => event.id === eventId);
    eventToUpdate.appointments.push({ date: appointment.date, time: appointment.time });
    setEvents([...events]);
    setAppointment({ eventId: '', date: '', time: '' });
  };

  const handleEditEvent = (event) => {
    setEditingEvent(event);
    setNewEvent({
      title: event.title,
      description: event.description,
      date: event.date,
      time: event.time,
    });
  };

  const handleDeleteEvent = (eventId) => {
    const updatedEvents = events.filter((event) => event.id !== eventId);
    setEvents(updatedEvents);
  };

  return (
    <div className="manage-events">
      <div className="manage-events-container">
        <h1>Gestión de Eventos y Citas</h1>

        {role === 'admin' && (
          <div className="event-card">
            <h2>{editingEvent ? 'Editar Evento' : 'Crear Nuevo Evento'}</h2>
            <input
              type="text"
              name="title"
              placeholder="Título del Evento"
              value={newEvent.title}
              onChange={handleEventChange}
            />
            <textarea
              name="description"
              placeholder="Descripción del Evento"
              value={newEvent.description}
              onChange={handleEventChange}
            />
            <div className="input-group">
              <input
                type="date"
                name="date"
                value={newEvent.date}
                onChange={handleEventChange}
              />
              <input
                type="time"
                name="time"
                value={newEvent.time}
                onChange={handleEventChange}
              />
            </div>
            <button onClick={handleCreateEvent}>
              {editingEvent ? 'Actualizar Evento' : 'Crear Evento'}
            </button>
          </div>
        )}

        <h2>Eventos Disponibles</h2>
        <div className="event-container">
          {events.map((event) => (
            <div key={event.id} className="event-card">
              <div>
                <h3>{event.title}</h3>
                <p>{event.description}</p>
                <div className="event-date-time">
                  <span>📅 {event.date}</span>
                  <span>⏰ {event.time}</span>
                </div>
              </div>

              {role === 'admin' && (
                <div className="event-actions">
                  <button onClick={() => handleEditEvent(event)}>Editar</button>
                  <button onClick={() => handleDeleteEvent(event.id)}>Eliminar</button>
                </div>
              )}

              {role === 'client' && (
                <div className="appointment-section">
                  <h3>Agendar Cita</h3>
                  <div className="input-group">
                    <input
                      type="date"
                      name="date"
                      value={appointment.date}
                      onChange={handleAppointmentChange}
                    />
                    <input
                      type="time"
                      name="time"
                      value={appointment.time}
                      onChange={handleAppointmentChange}
                    />
                  </div>
                  <button onClick={() => handleScheduleAppointment(event.id)}>Agendar</button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ManageEvents;
