import React from 'react';
import './ManageEvents.css';

function ManageEvents({ role }) {
  return (
    <div className="manage-events">
      <h1>Gestión de Eventos</h1>
      <p>Bienvenido {role === 'admin' ? 'Administrador' : 'Cliente'}, aquí puedes gestionar los eventos y capacitaciones.</p>

      <div className="event-filter">
        <label>Filtrar por:</label>
        <select>
          <option value="all">Todos</option>
          <option value="upcoming">Próximos eventos</option>
          <option value="past">Eventos pasados</option>
        </select>
      </div>

      <div className="event-container">
        {/* Reemplaza esto con tus eventos */}
        <div className="event-card">
          <h2>Seminario de Tecnología Ecológica</h2>
          <p>Un evento sobre el uso de tecnologías sostenibles en la agricultura.</p>
          <div className="event-date">Fecha: 15 de diciembre, 2024</div>
        </div>
        <div className="event-card">
          <h2>Capacitación en Riego Automático</h2>
          <p>Aprende sobre sistemas de riego automatizado para grandes hectáreas.</p>
          <div className="event-date">Fecha: 20 de enero, 2025</div>
        </div>
      </div>

      {role === 'admin' && (
        <button className="add-event-button">Agregar nuevo evento</button>
      )}
    </div>
  );
}

export default ManageEvents;
