import React, { useState } from 'react';
import './Login.css'; // Asegúrate de que este archivo esté disponible

const Login = () => {
  const [username, setUsername] = useState("usuario@ejemplo.com"); // Valor predeterminado (dato real)
  const [password, setPassword] = useState(""); // Campo de contraseña vacío

  const handleLogin = (e) => {
    e.preventDefault();
    alert(`Iniciando sesión con el usuario: ${username}`);
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <div className="input-group">
          <label htmlFor="username">Correo Electrónico:</label>
          <input 
            type="email" 
            id="username" 
            value={username} 
            onChange={(e) => setUsername(e.target.value)} 
            required 
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Contraseña:</label>
          <input 
            type="password" 
            id="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
        </div>
        <button type="submit" className="btn-login">Iniciar sesión</button>
      </form>
      <p className="forgot-password">
        <a href="/forgot-password">¿Olvidaste tu contraseña?</a>
      </p>
    </div>
  );
};

export default Login;
