import React, { useState } from 'react';
import './ManageEvents.css';

function ManageEvents({ role }) {
  const [events, setEvents] = useState([
    { id: 1, title: 'Capacitaci√≥n de Riego Autom√°tico', date: '2024-11-20', time: '10:00 AM', description: 'Evento para aprender sobre riego autom√°tico.', appointments: [] },
    { id: 2, title: 'Sembrado Ecol√≥gico', date: '2024-11-22', time: '2:00 PM', description: 'Capacitaci√≥n sobre t√©cnicas de sembrado ecol√≥gico.', appointments: [] },
  ]);

  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
  });

  const [appointment, setAppointment] = useState({
    eventId: '',
    date: '',
    time: '',
  });

  const handleEventChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const handleCreateEvent = () => {
    setEvents([...events, { ...newEvent, id: events.length + 1, appointments: [] }]);
    setNewEvent({ title: '', description: '', date: '', time: '' });
  };

  const handleAppointmentChange = (e) => {
    setAppointment({ ...appointment, [e.target.name]: e.target.value });
  };

  const handleScheduleAppointment = (eventId) => {
    const eventToUpdate = events.find((event) => event.id === eventId);
    eventToUpdate.appointments.push({ date: appointment.date, time: appointment.time });
    setEvents([...events]);
    setAppointment({ eventId: '', date: '', time: '' });
  };

  return (
    <div className="manage-events">
      <div className="manage-events-container">
        <h1>Gesti√≥n de Eventos y Citas</h1>

        {role === 'admin' && (
          <div className="event-card">
            <h2>Crear Nuevo Evento</h2>
            <input
              type="text"
              name="title"
              placeholder="T√≠tulo del Evento"
              value={newEvent.title}
              onChange={handleEventChange}
            />
            <textarea
              name="description"
              placeholder="Descripci√≥n del Evento"
              value={newEvent.description}
              onChange={handleEventChange}
            />
            <div className="input-group">
              <input
                type="date"
                name="date"
                value={newEvent.date}
                onChange={handleEventChange}
              />
              <input
                type="time"
                name="time"
                value={newEvent.time}
                onChange={handleEventChange}
              />
            </div>
            <button onClick={handleCreateEvent}>Crear Evento</button>
          </div>
        )}

        <h2>Eventos Disponibles</h2>
        <div className="event-container">
          {events.map((event) => (
            <div key={event.id} className="event-card">
              <div>
                <h3>{event.title}</h3>
                <p>{event.description}</p>
                <div className="event-date-time">
                  <span>üìÖ {event.date}</span>
                  <span>‚è∞ {event.time}</span>
                </div>
              </div>

              {role === 'admin' && (
                <div className="appointments-section">
                  <h4>Citas Agendadas</h4>
                  {event.appointments.length > 0 ? (
                    <ul>
                      {event.appointments.map((appointment, index) => (
                        <li key={index}>
                          {appointment.date} - {appointment.time}
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p>No hay citas agendadas para este evento.</p>
                  )}
                </div>
              )}

              {role === 'client' && (
                <div className="appointment-section">
                  <h3>Agendar Cita</h3>
                  <div className="input-group">
                    <input
                      type="date"
                      name="date"
                      value={appointment.date}
                      onChange={handleAppointmentChange}
                    />
                    <input
                      type="time"
                      name="time"
                      value={appointment.time}
                      onChange={handleAppointmentChange}
                    />
                  </div>
                  <button onClick={() => handleScheduleAppointment(event.id)}>Agendar</button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ManageEvents;
